import React from 'react';

function Greet() {
  const date =new Date()
  const hours = date.getHours()
  let timeOfDay
  const styles = {fontSize: 30}
  if (hours<12 && hours>3){
    timeOfDay="morning"
    styles.color="#04756F"}
  else if(hours >=12 && hours <17){
    timeOfDay="afternoon"
    styles.color="#891413"}
  else if(hours >=17 && hours <=3) {
    timeOfDay="night"
    styles.color="#D90000"}
  return (<h1 style={styles}>Good {timeOfDay} {hours}! </h1>);
}

export default Greet;
