import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Spinner from 'react-bootstrap/Spinner';
import Greet from './Greet'




function App() {

  return (
    <div className="AppBox">

      <Greet/>
      <Spinner animation="grow" variant="info" />
      <Form>
        <Form.Label>Username</Form.Label>
        <Form.Control placeholder="username" />
        <Form.Group controlId="formGroupEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="name@example.com" />
          <small id="emailHelp" class="form-text text-muted">Your data will remain secure with us</small>
        </Form.Group>
        <Form.Group controlId="exampleForm.ControlSelect1">
          <Form.Label>Select a product </Form.Label>
          <Form.Control as="select">
            <option>Red ded Redumption</option>
            <option>Assassin creed Valhalla</option>
            <option>Watch Dogs legion</option>
            <option>Last of Us</option>
            <option>Ghost of tsushima</option>
          </Form.Control>
        </Form.Group>
        <Form.Control/>
        <Form.Group controlId="exampleForm.ControlSelect1">
        <textarea rows="4" cols="50" name="comment" form="usrform">
        Enter your review here...</textarea>
        </Form.Group>
        <Form.Control placeholder="username" />
        /*most trusted measurement of quality for Games ,Movies , TV.*/
        <Button type="submit" className="mb-2">
        Submit
        </Button>
      </Form>

    </div>

  );
}

export default App;
